        //unsubscribing to some events
        StarterAssetsInputs.OnItemAction -= OnItemAction;
        StarterAssetsInputs.OnLeftClickedAction -= OnLeftClickedAction;
        StarterAssetsInputs.OnRightClickedAction -= OnRightClickedAction;
